import { useState, useEffect } from 'react';
import { Room, createLocalAudioTrack, Track } from 'livekit-client';

function App() {
  const [room, setRoom] = useState(null);
  const [connected, setConnected] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);

  useEffect(() => {
    if (!room) return;

    room.on('trackSubscribed', (track, publication, participant) => {
      console.log(`Track subscribed: ${track.kind}`);

      if (track.kind === Track.Kind.Audio) {
        setIsSpeaking(true);

        const audioElement = track.attach();
        document.body.appendChild(audioElement);

        audioElement.onended = () => {
          setIsSpeaking(false);
          audioElement.remove();
        };
      }
    });

    room.on('trackUnsubscribed', (track, publication, participant) => {
      console.log(`Track unsubscribed: ${track.kind}`);

      if (track.kind === Track.Kind.Audio) {
        track.detach().forEach((element) => element.remove());
        setIsSpeaking(false);
      }
    });

    // ðŸ§  Listen for real-time transcription or agent responses!
    room.on('dataReceived', (payload, participant, kind) => {
      const message = new TextDecoder().decode(payload);
      console.log(`Data message received from ${participant.identity}: ${message}`);

      setChatMessages(prev => [...prev, { sender: participant.identity, text: message }]);
    });

    return () => {
      room.off('trackSubscribed');
      room.off('trackUnsubscribed');
      room.off('dataReceived');
    };
  }, [room]);

  const connectToRoom = async () => {
    try {
      const resp = await fetch('http://localhost:8000/api/token?room=demo-room&user=guest-user');
      const data = await resp.json();
      const token = data.token;

      const newRoom = new Room();
      await newRoom.connect("wss://mre-2-qwlub3b3.livekit.cloud", token);

      setRoom(newRoom);
      setConnected(true);

      const micTrack = await createLocalAudioTrack();
      await newRoom.localParticipant.publishTrack(micTrack);

      console.log('Connected and microphone publishing.');
    } catch (err) {
      console.error('Error connecting to LiveKit:', err);
    }
  };

  const disconnectFromRoom = async () => {
    if (room) {
      await room.disconnect();
      setConnected(false);
      setRoom(null);
      setChatMessages([]);
      console.log('Disconnected.');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '5%' }}>
      <h1>LiveKit AI Agent Interaction</h1>

      {!connected ? (
        <button onClick={connectToRoom} style={{ padding: '10px 20px', fontSize: '18px' }}>
          Start Call
        </button>
      ) : (
        <button onClick={disconnectFromRoom} style={{ padding: '10px 20px', fontSize: '18px' }}>
          End Call
        </button>
      )}

      {connected && (
        <>
          {isSpeaking && (
            <div style={{ marginTop: '20px', fontWeight: 'bold', fontSize: '20px', color: 'green' }}>
              ðŸ”Š Agent is speaking...
            </div>
          )}

          <div style={{
            marginTop: '40px',
            border: '1px solid gray',
            borderRadius: '8px',
            width: '400px',
            marginLeft: 'auto',
            marginRight: 'auto',
            padding: '20px',
            textAlign: 'left'
          }}>
            <h3>Chat</h3>
            <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
              {chatMessages.map((msg, idx) => (
                <div key={idx} style={{ marginBottom: '10px' }}>
                  <b>{msg.sender}:</b> {msg.text}
                </div>
              ))}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
