import { useState } from 'react';
import { Room, createLocalAudioTrack } from 'livekit-client';

function App() {
  const [room, setRoom] = useState(null);
  const [connected, setConnected] = useState(false);

  const connectToRoom = async () => {
    try {
      const resp = await fetch('http://localhost:8000/api/token?room=demo-room&user=guest-user');
      const data = await resp.json();
      const token = data.token;

      const newRoom = new Room();
      await newRoom.connect("wss://mre-2-qwlub3b3.livekit.cloud", token);

      setRoom(newRoom);
      setConnected(true);

      // ✅ Create and publish microphone track manually
      const micTrack = await createLocalAudioTrack();
      await newRoom.localParticipant.publishTrack(micTrack);

      console.log('Connected to LiveKit Room and microphone is publishing!');
    } catch (err) {
      console.error('Error connecting to LiveKit:', err);
    }
  };

  const disconnectFromRoom = async () => {
    if (room) {
      await room.disconnect();
      setConnected(false);
      setRoom(null);
      console.log('Disconnected from LiveKit.');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '10%' }}>
      <h1>LiveKit AI Agent Interaction</h1>
      {!connected ? (
        <button onClick={connectToRoom} style={{ padding: '10px 20px', fontSize: '18px' }}>
          Start Call
        </button>
      ) : (
        <button onClick={disconnectFromRoom} style={{ padding: '10px 20px', fontSize: '18px' }}>
          End Call
        </button>
      )}
    </div>
  );
}

export default App;
